pan&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;params&#34;&gt;            RedisConnectionFactory redisConnectionFactory)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;throws&lt;/span&gt; UnknownHostException &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        Jackson2JsonRedisSerializer&amp;lt;Object&amp;gt; jackson2JsonRedisSerializer = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;title class_&#34;&gt;Jackson2JsonRedisSerializer&lt;/span&gt;&amp;lt;Object&amp;gt;(Object.class);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;type&#34;&gt;ObjectMapper&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;om&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;title class_&#34;&gt;ObjectMapper&lt;/span&gt;();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        om.setVisibility(PropertyAccessor.ALL, JsonAutoDetect.Visibility.ANY);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        om.enableDefaultTyping(ObjectMapper.DefaultTyping.NON_FINAL);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        jackson2JsonRedisSerializer.setObjectMapper(om);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        RedisTemplate&amp;lt;String, Object&amp;gt; template = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;title class_&#34;&gt;RedisTemplate&lt;/span&gt;&amp;lt;String, Object&amp;gt;();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        template.setConnectionFactory(redisConnectionFactory);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        template.setKeySerializer(jackson2JsonRedisSerializer);&lt;/span&gt;&lt;br