an class=&#34;line&#34;&gt;68&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;@RestController&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;@RequestMapping(&amp;quot;/test&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;public class TestController &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    public static ExecutorService threadPool =&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            new ThreadPoolExecutor(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    10,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    40,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    20,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    TimeUnit.SECONDS,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    new ArrayBlockingQueue&amp;lt;Runnable&amp;gt;(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                            16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    ));&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    /**&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;     * CompletableFuture 测试&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;     *&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;     * @return&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;     */&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    @ApiOperation(&amp;quot;CompletableFuture 测试&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    @PostMapping(&amp;quot;test&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    public Object test() &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        Map&amp;lt;